// src/components/AddPersonModal.js

import React, { useState } from 'react';
import '../Style/AddTaskModal.css'; // Import the CSS for styling the modal

const AddTaskModal = ({ isVisible, onClose, onAdd }) =>
{
    const [newTask, setNewTask] = useState({
        name: '',
        age: '',
        city: '',
        country: '',
    });

    // Function to handle input changes
    const handleChange = (e) =>
    {
        const { name, value } = e.target;
        setnewTask((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    // Function to handle adding a new person
    const handleAddnewTask = (e) =>
    {
        e.preventDefault();
        if (newTask.name && newTask.age && newTask.city && newTask.country)
        {
            onAdd({ ...newTask, age: parseInt(newTask.age) }); // Pass the new person data to the parent
            setnewTask({ name: '', age: '', city: '', country: '' }); // Reset form
            onClose(); // Close the modal
        } else
        {
            alert("Please fill out all fields."); // Simple validation
        }
    };

    if (!isVisible) return null; // If not visible, don't render anything

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <h3>Add New Task</h3>
                <form onSubmit={handleAddnewTask}>
                    <input
                        type="text"
                        name="Description"
                        placeholder="Task Description"
                        value={newTask.name}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="number"
                        name="Status"
                        placeholder="Task Status"
                        value={newTask.age}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="Date"
                        placeholder="Date"
                        value={newTask.Date}
                        onChange={handleChange}
                        required
                    />

                    <button type="submit">Add New</button>
                    <button type="button" onClick={onClose}>Cancel</button>
                </form>
            </div>
        </div>
    );
};

export default AddTaskModal;
