import React, { useState, useEffect } from 'react';
// import 'bootstrap/dist/css/bootstrap.min.css';

interface Task {
    Description: string;
    Status: string;
    Date: string;
}

interface AddTaskModalProps {
    isVisible: boolean;
    onClose: () => void;
    onAdd: (task: Task) => void;
    task?: Task | null; 
}

const AddTaskModal: React.FC<AddTaskModalProps> = ({ isVisible, onClose, onAdd, task }) => {
    const [newTask, setnewTask] = useState<Task>({
        Description: '',
        Status: '',
        Date: '',
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setnewTask((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    useEffect(() => {
        if (task) {
            setnewTask({
                Description: task.Description || '',
                Status: task.Status || '',
                Date: task.Date || '',
            });
        } else {
            setnewTask({ Description: '', Status: '', Date: '' }); 
        }
    }, [task]);

    const handleAddOrUpdateTask = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        if (newTask.Description && newTask.Status && newTask.Date) {
            onAdd({
                Description: newTask.Description,
                Status: newTask.Status,
                Date: newTask.Date,
            });
            setnewTask({ Description: '', Status: '', Date: '' }); 
        } else {
            alert("Please fill out all fields."); 
        }
    };

    if (!isVisible) return null; 

    return (
        <div className="modal show" style={{ display: 'block', backgroundColor: 'rgba(0, 0, 0, 0.7)' }}>
            <div className="modal-dialog">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">{task ? 'Update Task' : 'Add New Task'}</h5>
                        <button type="button" className="close" onClick={onClose}>
                            <span>&times;</span>
                        </button>
                    </div>
                    <div className="modal-body">
                        <form onSubmit={handleAddOrUpdateTask}>
                            <div className="form-group">
                                <input
                                    type="text"
                                    name="Description"
                                    className="form-control"
                                    placeholder="Task Description"
                                    value={newTask.Description}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <input
                                    type="text"
                                    name="Status"
                                    className="form-control"
                                    placeholder="Task Status"
                                    value={newTask.Status}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <input
                                    type="text"
                                    name="Date"
                                    className="form-control"
                                    placeholder="Date"
                                    value={newTask.Date}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                            <button type="submit" className="btn btn-success">{task ? 'Update Task' : 'Add New'}</button>
                            <button type="button" className="btn btn-danger" onClick={onClose}>Cancel</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddTaskModal;
