// src/components/AddPersonModal.js

import React, { useState } from 'react';
import '../Style/AddPersonModal.css'; // Import the CSS for styling the modal

const AddPersonModal = ({ isVisible, onClose, onAdd }) =>
{
    const [newPerson, setNewPerson] = useState({
        name: '',
        age: '',
        city: '',
        country: '',
    });

    // Function to handle input changes
    const handleChange = (e) =>
    {
        const { name, value } = e.target;
        setNewPerson((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    // Function to handle adding a new person
    const handleAddNewPerson = (e) =>
    {
        e.preventDefault();
        if (newPerson.name && newPerson.age && newPerson.city && newPerson.country)
        {
            onAdd({ ...newPerson, age: parseInt(newPerson.age) }); // Pass the new person data to the parent
            setNewPerson({ name: '', age: '', city: '', country: '' }); // Reset form
            onClose(); // Close the modal
        } else
        {
            alert("Please fill out all fields."); // Simple validation
        }
    };

    if (!isVisible) return null; // If not visible, don't render anything

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <h3>Add New Task</h3>
                <form onSubmit={handleAddNewPerson}>
                    <input
                        type="text"
                        name="Desc"
                        placeholder="Name"
                        value={newPerson.name}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="number"
                        name="age"
                        placeholder="Age"
                        value={newPerson.age}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="city"
                        placeholder="City"
                        value={newPerson.city}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="country"
                        placeholder="Country"
                        value={newPerson.country}
                        onChange={handleChange}
                        required
                    />
                    <button type="submit">Add New</button>
                    <button type="button" onClick={onClose}>Cancel</button>
                </form>
            </div>
        </div>
    );
};

export default AddPersonModal;
