import React, { useState, useEffect } from 'react';

interface Task {
    Description: string; // corresponds to `name`
    Status: string;      // corresponds to `age`
    Date: string;        // corresponds to `city`
}

interface AddTaskModalProps {
    isVisible: boolean;
    onClose: () => void;
    onAdd: (task: Task) => void;
    task?: Task; // Optional prop for updating a task
}

const AddTaskModal: React.FC<AddTaskModalProps> = ({ isVisible, onClose, onAdd, task }) => {
    const [newTask, setNewTask] = useState<Task>({
        Description: '', // corresponds to `name`
        Status: '',      // corresponds to `age`
        Date: '',        // corresponds to `city`
    });

    useEffect(() => {
        if (task) {
            setNewTask({
                Description: task.Description || '', // Adjust property according to Task type
                Status: task.Status || '',
                Date: task.Date || '',
            });
        } else {
            setNewTask({ Description: '', Status: '', Date: '' }); // Reset when adding a new task
        }
    }, [task]);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setNewTask(prevTask => ({ ...prevTask, [name]: value }));
    };

    const handleAddOrUpdateTask = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        if (newTask.Description && newTask.Status && newTask.Date) {
            onAdd({
                Description: newTask.Description,
                Status: newTask.Status,
                Date: newTask.Date,
            });
            setNewTask({ Description: '', Status: '', Date: '' }); // Reset form
        } else {
            alert("Please fill out all fields."); // Simple validation
        }
    };

    if (!isVisible) return null;

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <h3>{task ? 'Update Task' : 'Add New Task'}</h3>
                <form onSubmit={handleAddOrUpdateTask}>
                    <input
                        type="text"
                        name="Description" // corresponds to `name`
                        placeholder="Task Description"
                        value={newTask.Description}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="Status" // corresponds to `age`
                        placeholder="Task Status"
                        value={newTask.Status}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="Date" // corresponds to `city`
                        placeholder="Date"
                        value={newTask.Date}
                        onChange={handleChange}
                        required
                    />

                    <button type="submit">{task ? 'Update Task' : 'Add New'}</button>
                    <button type="button" onClick={onClose}>Cancel</button>
                </form>
            </div>
        </div>
    );
};

export default AddTaskModal;
