// src/components/TableComponent.js

import React, { useState } from 'react';
import '../Style/TableComponent.css';
import AddPersonModal from './AddPersonModal'; // Import the modal component

const TableComponent = () => {
    // Sample data
    const [data, setData] = useState([
        { id: 1, Description: 'Alice', Status: 'Open', Date: 'May' },
        { id: 2, Description: 'Bob', Status: 'Open', Date: 'San Francisco'},
        { id: 3, Description: 'Charlie', Status: 'Open', Date: 'London' },
        { id: 4, Description: 'David', Status: 'Closed', Date: 'Berlin' },
        { id: 5, Description: 'Eva', Status: 'Closed', Date: 'Paris'},
    ]);

    // State to toggle modal visibility
    const [isModalVisible, setIsModalVisible] = useState(false);

    // Function to handle adding a new person
    const handleAddPerson = (newPerson) => {
        setData((prev) => [
            ...prev,
            {
                id: prev.length + 1, // Simple ID generation
                ...newPerson,
            },
        ]);
    };

    return (
        <div>
            <h2>Sample Table</h2>
            <table>
                <thead>
                    <tr>
                        <th>Ticket id</th>
                        <th>Description</th>
                        <th>Status</th>
                        <th>Date</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map(item => (
                        <tr key={item.id}>
                            <td>{item.id}</td>
                            <td>{item.Description}</td>
                            <td>{item.Status}</td>
                            <td>{item.Date}</td>
                            <td><a href="#" onclick="updateData">Update</a> <a href="#" onclick="DeleteData">Delete</a></td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <div className="add-person">
                <button onClick={() => setIsModalVisible(true)}>
                    Add New
                </button>
            </div>

            {/* AddPersonModal */}
            <AddPersonModal
                isVisible={isModalVisible}
                onClose={() => setIsModalVisible(false)}
                onAdd={handleAddPerson}
            />
        </div>
    );
};

export default TableComponent;
