import React, { useState } from 'react';
import '../Style/TableComponent.css';

const TableComponent = () => {
    // Sample data
    const [data, setData] = useState([
        { id: 1, name: 'Alice', age: 25, city: 'New York', country: 'USA' },
        { id: 2, name: 'Bob', age: 30, city: 'San Francisco', country: 'USA' },
        { id: 3, name: 'Charlie', age: 35, city: 'London', country: 'UK' },
        { id: 4, name: 'David', age: 40, city: 'Berlin', country: 'Germany' },
        { id: 5, name: 'Eva', age: 28, city: 'Paris', country: 'France' },
    ]);

    // State for new person data
    const [newPerson, setNewPerson] = useState({
        name: '',
        age: '',
        city: '',
        country: '',
    });

    // Function to handle input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewPerson((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    // Function to handle adding a new person
    const addNewPerson = () => {
        if (newPerson.name && newPerson.age && newPerson.city && newPerson.country) {
            setData((prev) => [
                ...prev,
                {
                    id: prev.length + 1, // Simple ID generation
                    ...newPerson,
                },
            ]);
            setNewPerson({ name: '', age: '', city: '', country: '' }); // Reset form
        } else {
            alert("Please fill out all fields."); // Simple validation
        }
    };

    return (
        <div>
            <h2>Sample Table</h2>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Age</th>
                        <th>City</th>
                        <th>Country</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map(item => (
                        <tr key={item.id}>
                            <td>{item.id}</td>
                            <td>{item.name}</td>
                            <td>{item.age}</td>
                            <td>{item.city}</td>
                            <td>{item.country}</td>
                        </tr>
                    ))}
                </tbody>
                <tr>
                <div className="add-person">

                <button onClick={addNewPerson}>Add New</button>

            </div>
            </tr>
            </table>

            {/* New Person Form */}

        </div>
    );
};

export default TableComponent;
