import React, { useState } from 'react';
import '../Style/TableComponent.css';
import AddTaskModal from './AddTaskModal'; // Import the modal component

const TableComponent = () =>
{
    // Sample data
    const [data, setData] = useState([
        { id: 1, Description: 'Promotion code issued', Status: 'Open', Date: 'May-29-2022' },
        { id: 2, Description: 'Additional user account', Status: 'Open', Date: 'May-29-2022' },
        { id: 3, Description: 'Change payment method', Status: 'Open', Date: 'May-29-2022' },
        { id: 4, Description: 'Activate account', Status: 'Closed', Date: 'May-29-2022' },
        { id: 5, Description: 'Great job', Status: 'Closed', Date: 'May-29-2022' },
    ]);

    // State to toggle modal visibility
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [editTask, setEditTask] = useState(null); // Track the task being edited

    // Function to handle adding/updating a task
    const handleAddOrUpdateTask = (newTask) =>
    {
        if (editTask)
        {
            // Update existing task
            setData((prev) =>
                prev.map((task) => (task.id === editTask.id ? { ...task, ...newTask } : task))
            );
            setEditTask(null); // Reset after updating
        } else
        {
            // Add new task
            setData((prev) => [
                ...prev,
                {
                    id: prev.length + 1, // Simple ID generation
                    ...newTask,
                },
            ]);
        }
        setIsModalVisible(false); // Close modal
    };

    // Function to handle deleting a task
    const handleDeleteTask = (id) =>
    {
        setData((prev) => prev.filter((task) => task.id !== id));
    };

    // Function to handle updating a task
    const handleUpdateTask = (task) =>
    {
        setEditTask(task); // Set the task to be edited
        setIsModalVisible(true); // Open the modal for editing
    };

    return (
        <div>
            <h3>Task List</h3>
            <table>
                <thead>
                    <tr>
                        <th>Ticket id</th>
                        <th>Description</th>
                        <th>Status</th>
                        <th>Date</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((item) => (
                        <tr key={item.id}>
                            <td>{item.id}</td>
                            <td>{item.Description}</td>
                            <td>{item.Status}</td>
                            <td>{item.Date}</td>
                            <td>
                                <a href="#" onClick={(e) => { e.preventDefault(); handleUpdateTask(item); }}>Update</a>
                                {" | "}
                                <a href="#" onClick={(e) => { e.preventDefault(); handleDeleteTask(item.id); }}>Delete</a>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <div className="add-task">
                <button onClick={() => setIsModalVisible(true)}>
                    Add New
                </button>
            </div>

            {/* AddTaskModal */}
            <AddTaskModal
                isVisible={isModalVisible}
                onClose={() =>
                {
                    setIsModalVisible(false);
                    setEditTask(null); // Reset edit task when modal is closed
                }}
                onAdd={handleAddOrUpdateTask}
                task={editTask} // Pass the task to the modal for editing
            />
        </div>
    );
};

export default TableComponent;
