import React, { useState, useEffect } from 'react';

interface Task {
    name: string;
    age: string;
    city: string;
    country: string;
}

interface AddTaskModalProps {
    isVisible: boolean;
    onClose: () => void;
    onAdd: (task: Task) => void;
    task?: Task; // Optional prop for updating a task
}

const AddTaskModal: React.FC<AddTaskModalProps> = ({ isVisible, onClose, onAdd, task }) => {
    const [newTask, setNewTask] = useState<Task>({
        name: '',
        age: '',
        city: '',
        country: '',
    });

    useEffect(() => {
        if (task) {
            setNewTask({
                name: task.name || '', // Adjust property according to Task type
                age: task.age || '',
                city: task.city || '',
                country: task.country || '',
            });
        } else {
            setNewTask({ name: '', age: '', city: '', country: '' }); // Reset when adding a new task
        }
    }, [task]);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setNewTask(prevTask => ({ ...prevTask, [name]: value }));
    };

    const handleAddOrUpdateTask = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        if (newTask.name && newTask.age && newTask.city) {
            onAdd({
                name: newTask.name,
                age: newTask.age,
                city: newTask.city,
                country: newTask.country,
            });
            setNewTask({ name: '', age: '', city: '', country: '' }); // Reset form
        }
    };

    if (!isVisible) return null;

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <h3>{task ? 'Update Task' : 'Add New Task'}</h3>
                <form onSubmit={handleAddOrUpdateTask}>
                    <input
                        type="text"
                        name="name"
                        placeholder="Task Name"
                        value={newTask.name}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="age"
                        placeholder="Task Age"
                        value={newTask.age}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="city"
                        placeholder="Task City"
                        value={newTask.city}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="country"
                        placeholder="Task Country"
                        value={newTask.country}
                        onChange={handleChange}
                        required
                    />
                    <button type="submit">{task ? 'Update Task' : 'Add New'}</button>
                    <button type="button" onClick={onClose}>Cancel</button>
                </form>
            </div>
        </div>
    );
};

export default AddTaskModal;
